import { useEffect, useState } from "react";
import Modal from './Modal'
import axios from "axios";
import Form from './Form.js'
import api from './api.js'

function App() {
  //state
  const[data,setData]=useState([]);       
  const[show,setShow] =useState(false);

  //fetch for update
 const handleUpdate= async (id)=>{
    await axios.patch(`http://localhost:8080/update/${id}`)

  }
  //fetch for delete
  const handleDelete=async (id)=>{
   await axios.delete(`http://localhost:8080/delete/${id}`)


  }
  //fetch for get
  async function getStudents() {
    const response = await axios.get('http://localhost:8080/personnel');

    setData(response);
  }

  const showModal=()=>{
    setShow(true);
  }
  const hideModal=()=>{
    setShow(false);
  }

  useEffect(() => {
    getStudents();
  });

  return (
  <div>
      <table class="ui table">
      <thead>
        <tr>
          <th>Name</th>
          <th>Date Joined</th>
          <th>E-mail</th>
          <th>Called</th>
          <th>Delete</th>
          <th>Update</th>
        </tr>
      </thead>
      <tbody>
      {
      data.map((student)=>{
      return <tr key={student._id}>
        <td>{student.name}</td>
        <td>{student.date}</td>
        <td>{student.email}</td>
        <td>{student.called}</td>
        <td><button onClick={()=>handleDelete(student._id)} >Delete</button></td>
        <td><button onClick={()=>handleUpdate(student._id)} >Update</button></td>
        </tr>
        })
      }
        </tbody>
      </table>
      <button class="ui button" onClick={showModal}>
          Add
      </button>
      <Modal show={show} handleClose={hideModal}>
          <Form/>
      </Modal>
  </div>
  );
  }

export default App;
