import {useState} from 'react'
import Nav from './Nav.js'
import { useNavigate } from 'react-router-dom';

const Form=()=>{
    let navigate = useNavigate();
    const[name,setName]=useState('');
    const[date,setDate]=useState('');
    const[email,setEmail]=useState('');
    const[called,setCalled]=useState('');

    const handlePost=()=>{
        const postURL = "http://localhost:8080/send" //Our previously set up route in the backend
        fetch(postURL, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ // We should keep the fields consistent for managing this data later
                name: name,
                date:date,
                email:email,
                called:called
                
            })
        })
    }
    
    const handleSubmit=(e)=> { // Once the form has been submitted, this function will post to the backend
        e.preventDefault()
        handlePost();
        alert("Student Added")
        navigate('/')
        
        
      }
      
    
    return(
        <div>
            <Nav/>
            <form class="ui form" onSubmit={handleSubmit}>
                <div class="fields">
                <div class="eight wide field">
                    <label>First Name</label>
                    <input type="text" value={name} onChange={(e)=>setName(e.target.value)} name="first-name" placeholder="First Name"/>
                </div>
                <div class="field">
                  <label>Date Joined</label>
                  <input type="text" value={date} onChange={(e)=>setDate(e.target.value)} name="last-name" placeholder="Last Name"/>
                </div>
                <div class="field">
                  <label>E-mail</label>
                  <input type="text" value={email} onChange={(e)=>setEmail(e.target.value)}  name="last-name" placeholder="Last Name"/>
                </div>              
                <div class="field">
                  <label>Called</label>
                  <input type="text" value={called} onChange={(e)=>setCalled(e.target.value)} name="last-name" placeholder="Last Name"/>
                </div>              
                </div>
                <button class="ui button" type="submit">Submit</button> 
            </form>
        </div>
    )
}

export default Form
