import React from "react";
import { useNavigate } from 'react-router-dom';
import {useState,useEffect} from 'react'

const UpdateForm=()=>{
   
    let navigate = useNavigate();
  
    const[name,setName]=useState('');
    const[date,setDate]=useState('');
    const[email,setEmail]=useState('');
    const[called,setCalled]=useState('');

    useEffect(() => {
      setID(localStorage.getItem('ID'))
      setFirstName(localStorage.getItem('First Name'));
      setLastName(localStorage.getItem('Last Name'));
      setCheckbox(localStorage.getItem('Checkbox Value'));
      }, []);

    const handleUpdate=()=>{
        const updateURL = `http://localhost:8080/update/${props.id}` //Our previously set up route in the backend
        fetch(updateURL, {
            method: 'PATCH',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ // We should keep the fields consistent for managing this data later
                name: name,
                date:date,
                email:email,
                called:called
                
            })
        })
    }
   
    const handleSubmit=(e)=> { // Once the form has been submitted, this function will post to the backend
        e.preventDefault()
        handleUpdate();
        alert("Student Updated")
        props.hideModal()
        navigate(0)
        
        
      }
      
     return(
        <div>
        
        <br/>
        <div class="ui container">
        
          <form  key={student._id } class="ui form" onSubmit={handleSubmit}>
            <div class="fields">
                
                <div>class="eight wide field">
                    <label>First Name</label>
                    <input  required type="text"  defaultValue={student.name}  onChange={e => setName(e.target.value)} name="first-name" placeholder="First Name"/>
                </div>
                <div   class="field">
                  <label>Date Joined</label>
                  <input  required  type="date"  defaultValue={student.date} onChange={e => setDate(e.target.value)} name="last-name" placeholder="Last Name"/>
                </div>
                <div class="field">
                  <label>E-mail</label>
                  <input requried  type="email" defaultValue={student.email}  onChange={e => setEmail(e.target.value)}  name="last-name" placeholder="Email"/>
                </div>              
                <div class="field">
                  <label>Called</label>
                  <input required  type="text"  defaultValue={student.called}   onChange={e => setCalled(e.target.value)} name="last-name" placeholder="Called"/>
                </div> 
                
            </div>
     
            <button class="ui button" type="submit">Submit</button> 
        </form>
         </div>
       
    </div>
    )
}
export default UpdateForm
