import { useEffect, useState } from "react";
import Modal from './Modal'
import axios from "axios";

function App() {
const[show,setShow] =useState(false);
const[data,setData]=useState([]);
const[name,setName]=useState('');
const[date,setDate]=useState('');
const[email,setEmail]=useState('');
const[called,setCalled]=useState('')

const handleSubmit=()=> { // Once the form has been submitted, this function will post to the backend
  const postURL = "http://localhost:8080/personnel" //Our previously set up route in the backend
  fetch(postURL, {
      method: 'POST',
      headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
      },
      body: JSON.stringify({ // We should keep the fields consistent for managing this data later
          name: name,
          date:date,
          email:email,
          called:called

      })
  })
  .then(()=>{
      // Once posted, the user will be notified 
      alert('You have been added to the system!');
  })
}

const showModal=()=>{
  setShow(true);
}
const hideModal=()=>{
  setShow(false);
}

async function getStudents() {
  const response = await fetch('http://localhost:8080/personnel');
  const da = await response.json();
  setData(da);
}
 useEffect(() => {
  getStudents();
 }, []);
console.log(data)

  return (
    <div >
      <table class="ui table">
  <thead>
    <tr>
      <th>Name</th>
      <th>Date Joined</th>
      <th>E-mail</th>
      <th>Called</th>
    </tr>
  </thead>
  <tbody>
 {
 data.map((student)=>{
 return <tr key={student._id}>
   <td>{student.name}</td>
   <td>{student.date}</td>
   <td>{student.email}</td>
   <td>{student.called}</td>
   </tr>

})

}

    </tbody>
</table>
<button class="ui button" onClick={showModal}>
  Add
</button>
<Modal show={show} handleClose={hideModal}>
<form class="ui form" onSubmit={handleSubmit}>
  <div class="field">
    <label>First Name</label>
    <input type="text" value={name} onChange={(e)=>setName(e.target.value)} name="first-name" placeholder="First Name"/>
  </div>
  <div class="field">
    <label>Date Joined</label>
    <input type="text" value={date} onChange={(e)=>setDate(e.target.value)} name="last-name" placeholder="Last Name"/>
  </div>
  <div class="field">
    <label>E-mail</label>
    <input type="text" value={email} onChange={(e)=>setEmail(e.target.value)}  name="last-name" placeholder="Last Name"/>
  </div>
  <div class="field">
    <label>Called</label>
    <input type="text" value={called} onChange={(e)=>setCalled(e.target.value)} name="last-name" placeholder="Last Name"/>
  </div>

    <button class="ui button" type="submit">Submit</button>
</form>
        </Modal>
    </div>
  );
}

export default App;
