import { useEffect, useState } from "react";
import axios from "axios";

import Nav from './Nav.js'

const Home=()=> {
  //state
  const[data,setData]=useState([]);       

  //fetch for update
  const handleUpdate=(id)=>{
    fetch(`http://localhost:8080/update/${id}`,{
      method:'PATCH'
    })
    getStudents();
  }
  //fetch for update
  const handleDelete=(id)=>{
   axios.delete(`http://localhost:8080/delete/${id}`)
   getStudents();

  }

  async function getStudents() {
    const response = await fetch('http://localhost:8080/personnel');
    const da = await response.json();
    setData(da);

   /*const response= await axios.get('http://localhost:8080/personnel')
    setData(response)*/  
  }

  useEffect(() => {
    getStudents();
  }, []);

  return (
  <div>
      <Nav/>
      <table class="ui celled table">
      <thead>
        <tr>
          <th>Name</th>
          <th>Date Joined</th>
          <th>E-mail</th>
          <th>Called</th>
          <th>Delete</th>
          <th>Update</th>
        </tr>
      </thead>
      <tbody>
      {
      data.map((student)=>{
      return <tr key={student._id}>
        <td>{student.name}</td>
        <td>{student.date}</td>
        <td>{student.email}</td>
        <td>{student.called}</td>
        <td><button onClick={()=>handleDelete(student._id)} >Delete</button></td>
        <td><button onClick={()=>handleUpdate(student._id)} >Update</button></td>
        </tr>
        })
      }
        </tbody>
      </table>


  </div>
  );
  }

export default Home;
