import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Nav from './Nav.js'
import Modal from './Modal.js'
import UpdateForm from './UpdateForm.js'

const Home=()=> {
  //state
  const[data,setData]=useState([]);
  const[show,setShow]=useState(false);  
  const[id,setId]=useState('')
  //For refresh     
  const navigate = useNavigate();

  //fetch for update
    /* const handleUpdate= (id)=>{
     fetch(`http://localhost:8080/update/${id}`,{
      method:'PATCH'
    })
    navigate(0)
     } */
  //fetch for update
  const handleDelete= (id)=>{
    axios.delete(`http://localhost:8080/delete/${id}`)
   navigate(0)
  }


  async function getStudents() {
   /*  const response = await fetch('http://localhost:8080/personnel');
    const da = await response.json();
    setData(da); */

    const response= await axios.get('http://localhost:8080/personnel')

    setData(response.data)  
   }

  useEffect(() => {
    getStudents();
  }, []);

  const showModal = () => {
    setShow(true );
  };

  const hideModal = () => {
    setShow(false);

  };

  const handleClick=(id)=>{
    showModal()
    setId(id)
  }

  return (

  <div >
    {console.log(id)}
      <Nav/>
      <br></br>
      <div class="ui container">
      <div class="ui toggle checkbox">
        <input type="checkbox" name="public"/>
        <label>Update</label>
      </div>
      </div>
      <br></br>
      <Modal show={show} handleClose={hideModal}>
          <UpdateForm handleClick={handleClick} data={data}/>
        </Modal>
      <div class="ui container">
      <table class="ui selectable celled table">
      <thead>
        <tr>
          <th>Name</th>
          <th>Date Joined</th>
          <th>E-mail</th>
          <th>Called</th>
          <th>Delete</th>
        </tr>
      </thead>
      <tbody>
      {
      data.map((student)=>{
      return <tr onClick={()=>(handleClick(student._id))} 
      key={student._id}>
        <td data-label="Name">{student.name}</td>
        <td data-label="Date">{student.date}</td>
        <td data-label="Email">{student.email}</td>
        <td data-label="Called">{student.called}</td>
        <td data-label="Delete"><button onClick={()=>handleDelete(student._id)} >Delete</button></td>

        </tr>
        })
      }
        </tbody>
      </table>
      </div>


  </div>
  );
  }

export default Home;
