import React from "react";
import { useNavigate,useLocation } from 'react-router-dom';
import {useState,useEffect} from 'react'

const UpdateForm=(props)=>{
   
    const[name,setName]=useState('');
    const[date,setDate]=useState('');
    const[email,setEmail]=useState('');
    const[called,setCalled]=useState('');

   
     return(
        <div>
        {console.log(props.id)}
        <br/>
        <div class="ui container">
        
         {props.data && props.data.filter(student=>student._id ===props.id).map((student)=>
            {
            return <form key={student._id } class="ui form" >
            <div class="fields">
           
            <div class="eight wide field">
                <label>First Name</label>
                <input required type="text" value={student.name} onChange={(e)=>setName(e.target.value)} name="first-name" placeholder="First Name"/>
            </div>
            <div class="field">
              <label>Date Joined</label>
              <input required  type="text" value={student.date} onChange={(e)=>setDate(e.target.value)} name="last-name" placeholder="Last Name"/>
            </div>
            <div class="field">
              <label>E-mail</label>
              <input requried  type="email" value={student.email} onChange={(e)=>setEmail(e.target.value)}  name="last-name" placeholder="Email"/>
            </div>              
            <div class="field">
              <label>Called</label>
              <input required  type="text" value={student.called} onChange={(e)=>setCalled(e.target.value)} name="last-name" placeholder="Called"/>
            </div> 
         
            </div>
     
            <button class="ui button" type="submit">Submit</button> 
        </form>
        })}   
        </div>
       
    </div>
    )
}
export default UpdateForm
