import { useEffect, useState } from "react";
import Modal from './Modal'

function App() {
const[show,setShow] =useState(false);
const[data,setData]=useState([]);
const showModal=()=>{
  setShow(true);
}
const hideModal=()=>{
  setShow(false);
}
useEffect(() => {
  fetch("http://localhost:8080/personnel")
  .then(response => response.json())
      // 4. Setting *dogImage* to the image url that we received from the response above
  .then(da => setData(da))
},[])
const getStudents = async () => {
  const studs = await axios.get('https://randomuser.me/api/?page=1&results=10&nat=us');
  setUsers(studs);
 };

 useEffect(() => {
  getStudents();
 }, []);
console.log(data)

  return (
    <div >
      <table class="ui table">
  <thead>
    <tr>
      <th>Name</th>
      <th>Date Joined</th>
      <th>E-mail</th>
      <th>Called</th>
    </tr>
  </thead>
  <tbody>
 {
 data.map((student)=>{
 return <tr key={student.name}><td>{student.Name}</td></tr>

})
}
    </tbody>
</table>
<button class="ui button" onClick={showModal}>
  Add
</button>
<Modal show={show} handleClose={hideModal}>
<form class="ui form">
  <div class="field">
    <label>First Name</label>
    <input type="text" name="first-name" placeholder="First Name"/>
  </div>
  <div class="field">
    <label>Date Joined</label>
    <input type="text" name="last-name" placeholder="Last Name"/>
  </div>
  <div class="field">
    <label>E-mail</label>
    <input type="text" name="last-name" placeholder="Last Name"/>
  </div>
  <div class="field">
    <label>Called</label>
    <input type="text" name="last-name" placeholder="Last Name"/>
  </div>

    <button class="ui button" type="submit">Submit</button>
</form>
        </Modal>
    </div>
  );
}

export default App;
